function getdataindia()
{
  fetch('https://api.covid19india.org/data.json')
        .then(resp =>resp.json())
        .then(data =>
        { var conf=[];
            var confreco=[];
            var reco=[];
            var date=[];
            var array=data[Object.keys(data)[0]]
        for(i=0;i<array.length;i++)
        {
           date[i]=(array[i].date)
            reco[i]=parseInt(array[i].dailyrecovered);
            conf[i]=parseInt(array[i].dailyconfirmed);
            
            }
            for(i=0;i<array.length;i++){
                confreco[i]=conf[i]-reco[i];
        }
        
        var ctx = document.getElementById("line-chart");
        

var lineChart = new Chart(ctx, {
  type: 'line',
  data: {
    labels: date,
    datasets: [
      {
        label: "Diference of Confirmed and Recoverd cases per day",
        data: confreco,
        borderColor: 'cyan'
      }
    ]
  },options:{responsive:false}
});
})
      
}